mapper.insert:

insert into t_auction (auction_id, auction_name, auction_start_price,
auction_upset, auction_start_time, auction_end_time,
auction_pic, auction_pic_type, auction_desc
)
values (#{auctionId,jdbcType=VARCHAR}, #{auctionName,jdbcType=VARCHAR}, #{auctionStartPrice,jdbcType=DECIMAL},
#{auctionUpset,jdbcType=DECIMAL}, #{auctionStartTime,jdbcType=TIMESTAMP}, #{auctionEndTime,jdbcType=TIMESTAMP},
#{auctionPic,jdbcType=VARCHAR}, #{auctionPicType,jdbcType=VARCHAR}, #{auctionDesc,jdbcType=VARCHAR}
)

mapper.select:
select auction_id,auction_name,auction_desc,auction_start_time,auction_end_time,auction_start_price
from t_auction

    and auction_status=0

        and auction_name like #{map.auctionName}


        and auction_desc like #{map.auctionDesc}


        and auction_start_time = #{map.auctionStartTime}


        and auction_end_time = #{map.auctionEndTime}


        and auction_start_price like #{map.auctionStartPrice}


order by auction_start_time asc

select count(*)
from t_auction

    and auction_status=0

        and auction_name=#{map.auctionName}


        and auction_desc=#{map.auctionDesc}


        and auction_start_time=#{map.auctionStartTime}


        and auction_end_time=#{map.auctionEndTime}


        and auction_start_price like #{map.auctionStartPrice}


order by auction_start_time asc

mapper.update:
update t_auction
set auction_status = 1
where auction_id = #{id,jdbcType=VARCHAR};
